import React, { useEffect, useState } from 'react'
import './Display.css'
import Table from 'react-bootstrap/Table';
import axios from 'axios';
import 'react-tooltip/dist/react-tooltip.css'
import { Tooltip } from 'react-tooltip'

import { FiEdit } from 'react-icons/fi'
import { IoTrashOutline } from "react-icons/io5";
import { useNavigate } from 'react-router-dom';
const Display = () => {
    const navigate = useNavigate()
    const [users, setUsers] = useState([])
    const getUsersData = async () => {
        const res = await axios.get("https://655997ee6981238d054cb7f1.mockapi.io/crud")
        const data = res.data
        setUsers(data)
        console.log(data)

    }

    const handleFormPath = () =>{
        navigate("/")
    }
   
    const handleDelete = (id)=>{
      const res =  axios.delete(`https://655997ee6981238d054cb7f1.mockapi.io/crud/${id}`)
        console.log(id)
        .then(res=> {
            console.log(res.data)
           const updatedData = users.filter(item=> item.id !== id)
            setUsers(updatedData)
        })
    }
   

    useEffect(() => {
        getUsersData()

    }, [])


    const handleNavigate = (elem)=>{

        navigate("/display/edit",{state:{elem}})

    }


    return (
        <div className='display'>
            <Tooltip id='my-tooltip' />
<div className="viewFormContainer">
    <button onClick={handleFormPath}>Go To Form</button>
</div>
            <Table variant='dark' striped bordered hover>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Mobile</th>
                        <th>Address</th>
                        <th>Actions</th>

                    </tr>
                </thead>

                {
                    users.map((elem, id) => {
                        return <tbody key={id}>
                            <tr>
                                <td>{id + 1}</td>
                                <td>{elem.name}</td>
                                <td>{elem.email}</td>
                                <td>{elem.mobile}</td>
                                <td>{elem.address}</td>
                                <td className='actions'><FiEdit className='edit-Icon' data-tooltip-id="my-tooltip" data-tooltip-content="Edit" onClick={()=>{handleNavigate(elem)}}/> <IoTrashOutline className='delete-Icon' data-tooltip-id="my-tooltip" onClick={()=>handleDelete(id)} data-tooltip-content="Delete" /></td>
                            </tr>


                        </tbody>
                    })
                }
            </Table>
        </div>
    )
}

export default Display
