import React, { useState, createContext } from 'react'
import './Form.css'
import { useNavigate } from 'react-router-dom'
import axios from 'axios'
export const MyContext = createContext()

const Form = () => {
const [name, setName]= useState('')
const [email, setEmail]= useState('')
const [mobile, setMobile]= useState('')
const [address, setAddress]= useState('')

  const handleName = (e)=>{
    setName(e.target.value)
  }
  
  const handleEmail = (e)=>{
    setEmail(e.target.value)
  }

  const handleMobile = (e)=>{
    setMobile(e.target.value)
  }

  const handleAddress = (e)=>{
    setAddress(e.target.value)
  }
const handleSubmit = async()=>{
  
    const res = await  axios.post("https://655997ee6981238d054cb7f1.mockapi.io/crud",{
      name,
      email,
      mobile,
      address      
    })
    const data = res.data
  
    console.log(data)
}
    const navigate = useNavigate()
  return (
    <MyContext.Provider value={{name: name, email:email, mobile:mobile }}>
    <div className='container'>
         <button className="view" onClick={()=>{navigate('display')}}>View Lists</button>
     <form className='form'>
        <input type="text" onChange={handleName} value={name} placeholder='Name'/>
        <input type="email" onChange={handleEmail} value={email} placeholder='Email' />
        <input type="mobile" onChange={handleMobile} value={mobile} placeholder='Mobile' />
        <input type="text" onChange={handleAddress} value={address} placeholder='Address' />
     </form>
     <button type="submit" onClick={handleSubmit} className='submit'>Submit</button>
    </div>
    </MyContext.Provider>
 
 )
 
}

export default Form
